package week1.day2;

public class OddEvenCheck {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 19;
		
		if (num %2 != 0)
		{
			System.out.println("number "+num+" is odd");
			
		}
		
		else
		{
			System.out.println("number "+num+" is even ");
		}

	}

}
